mapper.update:
update video_examine set pass_date = #{passDate},examine_status = #{examineResult},
                         apply_id = #{applyId},review_comment = #{reviewComment}
where examine_id = #{examineId}

mapper.select:
select video_id,name,time,image,view_num,duration,comment_num,author_id,collection_num from video


        name like "%" #{videoName} "%"


        and type_id = #{typeId}


        and duration <= #{durationValue}



    order by ${sortName} desc
