//回复相关信息是的组件 
<template>
  <div class="replyItem">
    <div class="replyItem-Box">
      <div class="replyItem-Img"></div>
    </div>
    <div class="replyItem-other">
      <div class="replyItem-info">
        <div class="title1">{{replyInfo.nickname}}</div>
        <div class="title2">{{replyInfo.content}}</div>
      </div>
      <div class="replyItem-select">
        <div class="ItemData">{{replyInfo.time}}</div>
        <div class="title3">回复</div>
        <div class="ItemMore" @click="lockMore">点击详情</div>
        <detailsPopup ref="detailsPopup"></detailsPopup>
      </div>

    </div>
  </div>
</template>

<script>
import detailsPopup from './popup/detailsPopup.vue';
export default {
  components: { detailsPopup },
  name: "replyItem",
  props:['replyInfo'],
  methods:{
    lockMore(){
      this.$refs.detailsPopup.showPopup(this.replyInfo);
    }
  }
};
</script>

<style lang='less' scoped>
.replyItem {
  display: flex;
  justify-content: space-between;
  margin: 15px 10px;
  padding: 10px 5px;
  height: 60px;
  color: #666;
  // border: 2px solid #eee;
  .replyItem-Box {
    flex: 1;
    .replyItem-Img {
      margin-left: 15px;
      width: 49%;
      height: 75%;
      border-radius: 50%;
      background: red;
    }
  }
  .replyItem-other {
    flex: 10;
    .replyItem-info{
      .title1,.title2,.title3{
        font-size: 12px;
      }
    }
    .replyItem-select{
      display: flex;
          .ItemData {
      margin-right: 15px;
      font-size: 14px;
    }
    .ItemMore {
      margin-left: 8px;
      margin-right: 10px;
      font-size: 14px;
      cursor: pointer;
    }
    }

  }
}
</style>
