//用户的相关存储信息
import { data } from 'autoprefixer';
import {getVideoNavType} from '../Servers/videoService'
const video = {
    namespaced:true,   //开启匿名空间
    state:{
        videoNavType:{},  //视频的选择列表
    },
    actions:{
      //获取视屏的选择相关信息
      getNavType(context){
        getVideoNavType().then((res)=>{
          console.log(res);
          if(res.data.code === 200){
            let data = res.data.data;
            context.commit('GETUSERINFO',data);
          }
        })
      },
    },
    mutations:{
      GETNAVTYPE(state,data){
        state.videoNavType = data
      },

    },
    getters:{
      //返回用户的部分信息供头部使用
      getUserImg(state){
        //个人页面中上部分对应的内容
        let userImg = {}
        userImg.nickname = state.userInfo.nickname || {}
        userImg.image = state.userInfo.nickname || {}
        userImg.message = state.userInfo.message || {}
        return userImg
      },

      //返回用户的全部信息
      getUserInfo(state){
        return state.userInfo
      }
    }
}
export default video
